import { Row, StyledFormLabel, StyledInput } from './Styles';
import AddExpense from './AddExpense';
import useState from 'react-usestateref';

const IndividualExpenseRow = ({ i, expenses, setExpenses }) => {
  const personKey = `person-${i}`;
  const [additionalRows, setAdditionalRows] = useState([]);
  console.log('additionalRows', additionalRows);
  const handleRemoveRow = () => {
    console.log('removing row but additional rows are', additionalRows);
  };

  return (
    <div>
      <Row>
        <div>
          <StyledFormLabel>Person {i}</StyledFormLabel>
          <StyledInput
            id={`person-${i}-name`}
            placeholder="Name"
            variant="outlined"
            onChange={(e) =>
              setExpenses({
                ...expenses,
                [personKey]: {
                  name: e.target.value || `Person ${i}`,
                  amount: expenses[personKey]?.amount,
                },
              })
            }
          />
        </div>
        <div>
          <StyledFormLabel>Individual Amount</StyledFormLabel>
          <StyledInput
            className={`person-${i}-amount`}
            placeholder="Amount"
            variant="outlined"
            type="number"
          />
        </div>
        <AddExpense
          className={`person-${i}-amount`}
          additionalRows={additionalRows}
          setAdditionalRows={setAdditionalRows}
          handleRemoveRow={handleRemoveRow}
        />
      </Row>
      {additionalRows}
    </div>
  );
};

export default IndividualExpenseRow;
