import './App.css';
import * as React from 'react';
import Button from '@mui/joy/Button';
import Input from '@mui/joy/Input';
import styled from '@emotion/styled';
import SharedExpenseRow from './SharedExpenseRow';
import IndividualExpenseRows from './IndividualExpenseRows';

const Container = styled.div`
  width: 850px;
  margin: 0 auto;
  text-align: center;
`;

const App = () => {
  const defaultNnumOfPeople = 2;
  const [numOfPeople, setNumOfPeople] = React.useState(defaultNnumOfPeople);

  // { person-i: {amount: []} }
  const [individualExpenses, setIndividualExpenses] = React.useState({});

  const [sharedExpenses, setSharedExpenses] = React.useState({});
  const [total, setTotal] = React.useState(0);

  // {person-i: name}
  const namesObj = {};
  Object.entries(individualExpenses).map((expense, i) => {
    namesObj[expense[0]] = expense[1]?.name;
  });

  // const isDisabled = Object.keys(namesObj).length !== numOfPeople;
  // TODO: FIX DISABLED STATE LATER
  const isDisabled = false;

  const calculate = () => {
    // TODO: traverse in a loop

    const expenseMap = {};

    const individualExpenseName =
      document.getElementById(`person-1-name`).value;
    let individualExpenseAmt = 0;

    const amounts = document.getElementsByClassName(`person-1-amount`);
    // document
    //   .getElementsByClassName(`person-1-amount`)[0]
    //   .getElementsByTagName('input')[0].value;

    document
      .getElementsByClassName(`person-1-amount`)
      .forEach((a) => console.log('a', a));
    // .getElementsByTagName('input')[0].value;

    console.log(
      'individualExpense',
      individualExpenseName,
      individualExpenseAmt
    );
    // const sharedPeople = document.getElementsByClassName('shared-people');
    // const sharedAmount = document.getElementsByClassName('shared-amount');
  };

  return (
    <Container>
      <h1>Split Expenses</h1>
      <div>
        <h3>How Many People?</h3>
        <Input
          placeholder="# of People"
          type="number"
          value={numOfPeople}
          variant="outlined"
          onChange={(e) => setNumOfPeople(e.target.value)}
        />
      </div>
      <h3>Individual Expenses</h3>
      <IndividualExpenseRows
        numOfPeople={numOfPeople}
        expenses={individualExpenses}
        setExpenses={setIndividualExpenses}
      />
      <h3>Shared Expenses</h3>
      <SharedExpenseRow namesObj={namesObj} isDisabled={isDisabled} />
      <h3>Total</h3>
      <Input
        placeholder="Amount"
        variant="outlined"
        type="number"
        value={total}
        onChange={(e) => setTotal(e.target.value)}
        disabled={isDisabled}
      />
      <p />
      <Button disabled={isDisabled} onClick={() => calculate()}>
        CALCULATE
      </Button>
    </Container>
  );
};

export default App;
