import Input from '@mui/joy/Input';
import styled from '@emotion/styled';
import FormLabel from '@mui/joy/FormLabel';
import { Option, Select } from '@mui/joy';

const Container = styled.div`
  display: flex;
  justify-content: space-between;
`;

const Row = styled.div`
  margin-top: 16px;
`;

const StyledFormLabel = styled(FormLabel)`
  width: 400px;
`;

const StyledInput = styled(Input)`
  width: 400px;
`;

const SharedExpenseRow = ({ names, expenses, setExpenses }) => {
  const isDisabled = names.length === 0;
  //   const personKey = `person-${i}`;

  // TODO: take care of multiple values
  const optionsArr = names.map((name, i) => (
    <Option value={name} disabled={isDisabled}>
      {name}
    </Option>
  ));
  return (
    <Container>
      {/* <Row> */}
      <Select
        placeholder="Select multiple people"
        multiple={true}
        disabled={isDisabled}
      >
        {optionsArr}
      </Select>
      {/* </Row> */}
    </Container>
  );
};

export default SharedExpenseRow;
