import './App.css';
import * as React from 'react';
import Button from '@mui/joy/Button';
import Input from '@mui/joy/Input';
import styled from '@emotion/styled';
import FormLabel from '@mui/joy/FormLabel';
import IndividualExpenseRow from './IndividualExpenseRow';
import { Option, Select } from '@mui/joy';

const Container = styled.div`
  width: 850px;
  margin: 0 auto;
  text-align: center;
`;

const App = () => {
  const defaultNnumOfPeople = 2;
  const [numOfPeople, setNumOfPeople] = React.useState(defaultNnumOfPeople);
  const [expenses, setExpenses] = React.useState({}); // person-i: {name: "", amount: ""}

  console.log({ expenses });
  const individualExpenseRows = [];

  for (let i = 0; i < numOfPeople; i++) {
    individualExpenseRows.push(
      <IndividualExpenseRow
        i={i + 1}
        expenses={expenses}
        setExpenses={setExpenses}
      />
    );
  }

  const names = Object.values(expenses).map((expense) => expense.name);
  console.log(names);

  return (
    <Container>
      <h1>Split Expenses</h1>

      <div>
        <h3>How Many People?</h3>
        <Input
          placeholder="# of People"
          type="number"
          value={numOfPeople}
          variant="outlined"
          onChange={(e) => setNumOfPeople(e.target.value)}
        />
      </div>

      <h3>Individual Expenses</h3>
      {individualExpenseRows}

      <h3>Shared Expenses</h3>
      <Select placeholder="Select" multiple={true}>
        <Option>{names[0]}</Option>
        <Option>{names[1]}</Option>
      </Select>
      <p />
      <Button>CALCULATE</Button>
    </Container>
  );
};

export default App;
