import { StyledInput } from "./Styles";
import useStateRef from "react-usestateref";

const NameRows = ({ numOfPeople, names, setNames }) => {
  const NameRow = ({ i }) => {
    const personKey = `person-${i}`;
    const [currName, setCurrName, currNameRef] = useStateRef("");
    console.log("currName", currNameRef);
    return (
      <StyledInput
        id={`${personKey}-name`}
        placeholder={`Person ${i} Name`}
        variant="outlined"
        value={currNameRef.current}
        onChange={(e) => {
          setCurrName(e.target.value);
          setNames({
            ...names,
            [personKey]: e.target.value || `Person ${i}`,
          });
        }}
      />
    );
  };

  const nameRows = [];
  for (let i = 1; i <= numOfPeople; i++) {
    nameRows.push(<NameRow i={i} />);
  }

  return <>{nameRows}</>;
};
export default NameRows;
