import "./App.css";
import * as React from "react";
import Button from "@mui/joy/Button";
import Input from "@mui/joy/Input";
import styled from "@emotion/styled";
import FormLabel from "@mui/joy/FormLabel";

const Container = styled.div`
  width: 80%;
  margin: 0 auto;
  text-align: center;
`;

const IndividualExpenseRow = styled.div`
  display: flex;
`;
const App = () => {
  const defaultNnumOfPeople = 2;
  const [numOfPeople, setNumOfPeople] = React.useState(defaultNnumOfPeople);
  return (
    <Container>
      <h1>Split Expenses</h1>
      <FormLabel>How Many People?</FormLabel>
      <Input
        placeholder="# of People"
        type="number"
        min={defaultNnumOfPeople}
        value={numOfPeople}
        variant="outlined"
        onChange={(e) => setNumOfPeople(e.target.value)}
      />

      <p />
      <FormLabel>Person 1</FormLabel>
      <Input placeholder="Name" variant="outlined" />

      <p />
      <FormLabel>Person 2</FormLabel>
      <Input placeholder="Name" variant="outlined" />
      <p />
      <Button>CALCULATE</Button>
    </Container>
  );
};

export default App;
