import Input from '@mui/joy/Input';
import styled from '@emotion/styled';
import FormLabel from '@mui/joy/FormLabel';
import { Option, Select } from '@mui/joy';

const Container = styled.div`
  // display: flex;
  // justify-content: space-between;
`;

const Row = styled.div`
  margin-top: 16px;
  display: flex;
  justify-content: space-between;
`;

const StyledSelect = styled(Select)`
  width: 400px;
`;

const SharedExpenseRow = ({ namesObj, expenses, setExpenses, isDisabled }) => {
  const optionsArr = Object.entries(namesObj).map((name) => (
    <Option value={name[0]} disabled={isDisabled}>
      {name[1]}
    </Option>
  ));

  return (
    <Container>
      <Row>
        <Select
          placeholder="Select multiple people"
          multiple={true}
          disabled={isDisabled}
        >
          {optionsArr}
        </Select>
        <Input
          // id={`person-${i}-amount`}
          placeholder="Amount"
          variant="outlined"
          type="number"
          disabled={isDisabled}
          onChange={(e) =>
            // setExpenses({
            //   ...expenses,
            //   [personKey]: {
            //     name: expenses[personKey]?.name || `Person ${i}`,
            //     amount: e.target.value,
            //   },
            // })
            {}
          }
        />
      </Row>
    </Container>
  );
};

export default SharedExpenseRow;
