import Input from '@mui/joy/Input';
import styled from '@emotion/styled';
import FormLabel from '@mui/joy/FormLabel';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import RemoveCircleIcon from '@mui/icons-material/RemoveCircle';
import { useState } from 'react';
import { generateRandomString } from './utils';

const Row = styled.div`
  width: 100%;
  margin-top: 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const RightAlignedRow = styled.div`
  margin-top: 16px;
  display: flex;
  justify-content: flex-end;
`;

const StyledFormLabel = styled(FormLabel)`
  width: 400px;
`;

const StyledInput = styled(Input)`
  width: 400px;
`;

const StyledAddIcon = styled(AddCircleIcon)`
  margin-top: 18px;
  color: #a5c0cc;

  &:hover {
    cursor: pointer;
  }
`;

const StyledRemoveIcon = styled(RemoveCircleIcon)`
  margin-top: 18px;
  color: #d1626c;
  margin-left: 12px;

  &:hover {
    cursor: pointer;
  }
`;

const IndividualExpenseRow = ({ i, expenses, setExpenses }) => {
  const personKey = `person-${i}`;
  const [additionalRows, setAdditionalRows] = useState([]);
  console.log('additional', additionalRows);

  return (
    <div>
      <Row>
        <div>
          <StyledFormLabel>Person {i}</StyledFormLabel>
          <StyledInput
            id={`person-${i}-name`}
            placeholder="Name"
            variant="outlined"
            onChange={(e) =>
              setExpenses({
                ...expenses,
                [personKey]: {
                  name: e.target.value || `Person ${i}`,
                  amount: expenses[personKey]?.amount,
                },
              })
            }
          />
        </div>
        <div>
          <StyledFormLabel>Individual Amount</StyledFormLabel>
          <StyledInput
            className={`person-${i}-amount`}
            placeholder="Amount"
            variant="outlined"
            type="number"
          />
        </div>
        <StyledAddIcon
          onClick={() => {
            const uniqueId = generateRandomString();
            setAdditionalRows([
              ...additionalRows,
              <RightAlignedRow>
                <StyledInput
                  className={`person-${i}-amount`}
                  placeholder="Amount"
                  variant="outlined"
                  type="number"
                />
                <StyledRemoveIcon />
              </RightAlignedRow>,
            ]);
          }}
        />
      </Row>
      {additionalRows}
    </div>
  );
};

export default IndividualExpenseRow;
