import { useState } from "react";
import { StyledInput } from "./Styles";

const NameRows = ({ numOfPeople, names, setNames }) => {
  const [isNextDisabled, setIsNextDisabled] = useState(true);
  React.useEffect(() => {
    setIsDisabled(Object.keys(names).length !== numOfPeople);
  }, [names]);

  const NameRow = ({ i }) => {
    const personKey = `person-${i}`;
    const [currName, setCurrName] = useState("");
    return (
      <StyledInput
        id={`person-${i}-name`}
        placeholder={`Person ${i} Name`}
        variant="outlined"
        value={currName}
        onChange={(e) => {
          setCurrName(e.target.value);
          setNames({
            ...names,
            [personKey]: e.target.value || `Person ${i}`,
          });
        }}
      />
    );
  };

  const nameRows = [];
  for (let i = 1; i <= numOfPeople; i++) {
    nameRows.push(<NameRow i={i} />);
  }

  return <>{nameRows}</>;
};
export default NameRows;
