import { Option } from '@mui/joy';
import { Row, StyledInput, StyledSelect } from './Styles';
import useState from 'react-usestateref';

const SharedExpenseRow = ({ namesObj, isDisabled }) => {
  const [additionalRows, setAdditionalRows, additionalRowsRef] = useState([]);

  const optionsArr = Object.entries(namesObj).map((name) => (
    <Option value={name[0]} disabled={isDisabled}>
      {name[1]}
    </Option>
  ));

  return (
    <div>
      <Row>
        <StyledSelect
          placeholder="Select multiple people"
          multiple={true}
          disabled={isDisabled}
        >
          {optionsArr}
        </StyledSelect>
        <StyledInput
          className={`shared-amount`}
          placeholder="Amount"
          variant="outlined"
          type="number"
          disabled={isDisabled}
        />
      </Row>
      {additionalRows}
    </div>
  );
};

export default SharedExpenseRow;
