import "./App.css";
import * as React from "react";
import Button from "@mui/joy/Button";
import Input from "@mui/joy/Input";
import styled from "@emotion/styled";
import FormLabel from "@mui/joy/FormLabel";
import IndividualExpenseRow from "./IndividualExpenseRow";

const Container = styled.div`
  width: 80%;
  margin: 0 auto;
  text-align: center;
`;

const App = () => {
  const defaultNnumOfPeople = 2;
  const [numOfPeople, setNumOfPeople] = React.useState(defaultNnumOfPeople);

  const rowsArr = [];
  const generateIndividualRows = () => {
    for (let i = 0; i < numOfPeople; i++) {
      rowsArr.push(<IndividualExpenseRow i={i + 1} />);
    }
  };

  generateIndividualRows();

  return (
    <Container>
      <h1>Split Expenses</h1>
      <FormLabel>How Many People?</FormLabel>
      <Input
        placeholder="# of People"
        type="number"
        min={defaultNnumOfPeople}
        value={numOfPeople}
        variant="outlined"
        onChange={(e) => setNumOfPeople(e.target.value)}
      />
      {rowsArr}
      <p />
      <Button>CALCULATE</Button>
    </Container>
  );
};

export default App;
