import './App.css';
import * as React from 'react';
import Button from '@mui/joy/Button';
import Input from '@mui/joy/Input';
import styled from '@emotion/styled';
import FormLabel from '@mui/joy/FormLabel';
import IndividualExpenseRow from './IndividualExpenseRow';
import SharedExpenseRow from './SharedExpenseRow';
import IndividualExpenseRows from './IndividualExpenseRows';

const Container = styled.div`
  width: 850px;
  margin: 0 auto;
  text-align: center;
`;

const App = () => {
  const defaultNnumOfPeople = 2;
  const [numOfPeople, setNumOfPeople] = React.useState(defaultNnumOfPeople);

  const initialIndividualExpenses = {};
  // for (let i = 1; i <= numOfPeople; i++) {
  //   initialIndividualExpenses[`Person-${i}`] = {
  //     name: `Person ${i}`,
  //     amount: 0,
  //   };
  // }

  // { person-i: {name: "", amount: ""} }
  const [individualExpenses, setIndividualExpenses] = React.useState(
    initialIndividualExpenses
  );

  // { person-i: amount }
  const [sharedExpenses, setSharedExpenses] = React.useState({});
  const [total, setTotal] = React.useState(0);

  const names = Object.values(individualExpenses).map(
    (expense) => expense.name
  );

  console.log({ individualExpenses });
  console.log(names);

  return (
    <Container>
      <h1>Split Expenses</h1>
      <div>
        <h3>How Many People?</h3>
        <Input
          placeholder="# of People"
          type="number"
          value={numOfPeople}
          variant="outlined"
          onChange={(e) => setNumOfPeople(e.target.value)}
        />
      </div>
      <h3>Individual Expenses</h3>
      <IndividualExpenseRows
        numOfPeople={numOfPeople}
        expenses={individualExpenses}
        setExpenses={setIndividualExpenses}
      />
      <h3>Shared Expenses</h3>
      <SharedExpenseRow
        names={names}
        // expenses={sharedExpenses}
        // setExpenses={setSharedExpenses}
      />
      <h3>Total</h3>
      <Input
        placeholder="Amount"
        variant="outlined"
        type="number"
        value={total}
        onChange={(e) => setTotal(e.target.value)}
      />{' '}
      <p />
      <Button>CALCULATE</Button>
    </Container>
  );
};

export default App;
