import { useState } from "react";
import { StyledInput } from "./Styles";

const NameRow = ({ i, names, setNames, name }) => {
  const personKey = `person-${i}`;
  const [currName, setCurrName] = useState(name);

  return (
    <StyledInput
      id={`${personKey}-name`}
      placeholder={`Person ${i} Name`}
      variant="outlined"
      value={personKey}
      onChange={(e) => {
        setCurrName(e.target.value);
        setNames({
          ...names,
          [personKey]: e.target.value || `Person ${i}`,
        });
      }}
    />
  );
};

const NameRows = ({ numOfPeople, names, setNames }) => {
  const nameRows = [];
  for (let i = 1; i <= numOfPeople; i++) {
    nameRows.push(
      <NameRow
        i={i}
        names={names}
        setNames={setNames}
        name={names.length > 0 ? [i - 1] : ""}
      />
    );
  }

  return <>{nameRows}</>;
};
export default NameRows;
