import Input from '@mui/joy/Input';
import styled from '@emotion/styled';
import FormLabel from '@mui/joy/FormLabel';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import RemoveCircleIcon from '@mui/icons-material/RemoveCircle';

const Container = styled.div`
  // display: flex;
  // justify-content: space-between;
`;

const Row = styled.div`
  width: 100%;
  margin-top: 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const RightAlignedRow = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const StyledFormLabel = styled(FormLabel)`
  width: 400px;
`;

const StyledInput = styled(Input)`
  width: 400px;
`;

const StyledAddIcon = styled(AddCircleIcon)`
  margin-top: 18px;
`;

const IndividualExpenseRow = ({ i, expenses, setExpenses }) => {
  const personKey = `person-${i}`;

  return (
    <Container>
      <Row>
        <div>
          <StyledFormLabel>Person {i} *</StyledFormLabel>
          <StyledInput
            id={`person-${i}-name`}
            placeholder="Name"
            variant="outlined"
            onChange={(e) =>
              setExpenses({
                ...expenses,
                [personKey]: {
                  name: e.target.value || `Person ${i}`,
                  amount: expenses[personKey]?.amount,
                },
              })
            }
          />
        </div>
        <div>
          <StyledFormLabel>Individual Amount *</StyledFormLabel>
          <StyledInput
            className={`person-${i}-amount`}
            placeholder="Amount"
            variant="outlined"
            type="number"
          />
        </div>
      </Row>{' '}
      <StyledAddIcon />
      <RightAlignedRow>
        <StyledInput
          className={`person-${i}-amount`}
          placeholder="Amount"
          variant="outlined"
          type="number"
        />
      </RightAlignedRow>
      {/* TODO: add additional expenses */}
    </Container>
  );
};

export default IndividualExpenseRow;
